        static int getLowest(int a, int b, int c)
        {
            int lowest = a;
            if (b < lowest)
                lowest = b;
            if (c < lowest)
                lowest = c;
            return lowest;
        }
        public static string CreateMD5(string input)
        {
            // Use input string to calculate MD5 hash
            MD5 md5 = System.Security.Cryptography.MD5.Create();
            byte[] inputBytes = System.Text.Encoding.ASCII.GetBytes(input);
            byte[] hashBytes = md5.ComputeHash(inputBytes);

            // Convert the byte array to hexadecimal string
            StringBuilder sb = new StringBuilder();
            for (int i = 0; i < hashBytes.Length; i++)
            {
                sb.Append(hashBytes[i].ToString("X2"));
            }
            return sb.ToString();
        }
        public static bool containsThreeVowels(string word)
        {
            int count = 0;
            foreach (char letter in word.ToLower())
            {
                if (letter.Equals('a') || letter.Equals('e') || letter.Equals('i') || letter.Equals('o') || letter.Equals('u'))
                    count += 1;
            }
            if (count > 2)
                return true;
            else return false;
        }
        public static bool containsDoubleLetter(string word)
        {
            Regex regex = new Regex(@"(.)\1");
            Match match = regex.Match(word);
            if (match.Success)
                return true;
            else return false;
        }
        public static bool containsBad(string word)
        {
            if (word.Contains("ab") || word.Contains("cd") || word.Contains("pq") || word.Contains("xy"))
                return false;
            else return true;
        }

