        static void Day2()
        {
            string sourceFile = @"C:\Users\brandon.bishop\Desktop\input.txt";
            string[] input = File.ReadLines(sourceFile).ToArray();
            int needed = 0;
            foreach (string line in input)
            {
                string[] values = line.Split('x');
                int l = Convert.ToInt16(values[0]);
                int w = Convert.ToInt16(values[1]);
                int h = Convert.ToInt16(values[2]);
                int side1 = (2 * l * w);
                int side2 = (2 * w * h);
                int side3 = (2 * h * l);
                int boxArea = (side1 + side2 + side3);
                int lowest = getLowest(l * w, w * h, h * l);
                int needForThis = boxArea + lowest;
                needed = needed + needForThis;
            }
            Console.WriteLine(needed);
        }
        static void Day3()
        {
            string sourceFile = @"C:\Users\brandon.bishop\Desktop\input.txt";
            string input = File.ReadAllText(sourceFile);
            int[,] grid = new int[5000, 5000];
            int x = 2500;
            int y = 2500;
            grid[x, y] = grid[x, y] + 1;
            foreach (char direction in input)
            {
                if (direction == '^')
                {
                    y += 1;
                    grid[x, y] = grid[x, y] + 1;
                }
                else if (direction == 'v')
                {
                    y -= 1;
                    grid[x, y] = grid[x, y] + 1;
                }
                else if (direction == '>')
                {
                    x += 1;
                    grid[x, y] = grid[x, y] + 1;
                }
                else if (direction == '<')
                {
                    x -= 1;
                    grid[x, y] = grid[x, y] + 1;
                }
            }
            int gotPresent = 0;
            for (int i = 0; i < 5000; i++)
            {
                for (int j = 0; j < 5000; j++)
                {
                    if ((grid[i, j] != null) && grid[i, j] > 0)
                        gotPresent += 1;
                }
            }
            Console.WriteLine(gotPresent);
        }
        static void Day4()
        {
            string seed = "iwrupvqb";
            string hashValue = "0";
            int i = -1;
            while (!hashValue.StartsWith("00000"))
            {
                i++;
                hashValue = CreateMD5(seed + i.ToString());
            }
            Console.WriteLine(i);
        }
        static void Dayfive()
        {

            string sourceFile = @"C:\Users\brandon.bishop\Desktop\input.txt";
            string[] input = File.ReadLines(sourceFile).ToArray();
            List<string> niceList = new List<string>();
            foreach (string word in input)
            {

                bool hasThree = containsThreeVowels(word);
                bool hasDouble = containsDoubleLetter(word);
                bool notContain = containsBad(word);
                if (hasThree && hasDouble && notContain)
                    niceList.Add(word);
            }
            Console.WriteLine(niceList.Count);
        }
