                                grid[i, j] = 0;
                            else
                                grid[i, j] = 1;
                        }
                    }
                }
            }
            int lit = 0;
            for (int i = 0; i <= 999; i++)
            {
                for (int j = 0; j <= 999; j++)
                {
                    if (grid[i, j] == 1)
                        lit += 1;
                }
            }
            Console.WriteLine(lit);
        }

        static void Main(string[] args)
        {
            DaySix();
        }
    }
}
